import React from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  ImageBackground,
  FlatList,
} from "react-native";
import TransparentHeader from "../components/TransparentHeader";
import { LinearGradient } from "expo-linear-gradient";
import { Entypo } from "@expo/vector-icons";

const SeventhScreen = ({ navigation }) => {
  const DATA = [
    {
      title: "الحجم",
      imageUrl: require("../assets/seventh1.png"),
      btnText: "v (٩٥.)صغير",
    },
    {
      title: "الحجم",
      imageUrl: require("../assets/seventh2.png"),
      btnText: "v (٩٥.)صغير",
    },
    {
      title: "الحجم",
      imageUrl: require("../assets/seventh3.png"),
      btnText: "-    1     +",
    },
    {
      title: "الحجم",
      imageUrl: require("../assets/seventh4.png"),
      btnText: "v (٩٥.)صغير",
    },
  ];
  return (
    <View style={{ flex: 1 }}>
      <ImageBackground
        style={{ flex: 1 }}
        resizeMode="cover"
        source={{
          uri:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRXBBC53iUtmg9SwnXrFRSDWYu3aqTFcKi6yQ&amp;usqp=CAU",
        }}
      >
        <TransparentHeader />
        <View style={styles.ButtonWrapper}>
          <LinearGradient
            style={{ borderRadius: 20, width: "50%" }}
            colors={["#DE2E3B", "#E64440", "#EF5F46"]}
          >
            <TouchableOpacity
              onPress={() => navigation.navigate("Eighth")}
              style={{
                width: "100%",
                alignSelf: "center",
                paddingHorizontal: 10,

                marginTop: 10,
                marginBottom: 10,
                justifyContent: "center",
                alignItems: "center",
                // marginLeft: 10,
              }}
            >
              <Text style={{ fontSize: 30, color: "#fff" }}>خیرات اجافیہ </Text>
            </TouchableOpacity>
          </LinearGradient>

          <View style={{ flexDirection: "row" }}>
            <Image
              style={{ width: 120, height: 80 }}
              source={{
                uri:
                  "https://upload.wikimedia.org/wikipedia/commons/4/4b/Soay_ewe.jpg",
              }}
            />
            <Entypo
              name="cross"
              size={30}
              color="red"
              style={{ justifyContent: "flex-start", alignSelf: "flex-start" }}
            />
          </View>
        </View>

        <View style={styles.LargeBtnWrapper}>
          <FlatList
            numColumns={2}
            columnWrapperStyle={{ justifyContent: "space-evenly" }}
            keyExtractor={(item, index) => index.toString()}
            data={DATA}
            renderItem={({ item, index }) => {
              return (
                <View style={styles.LargeBtn}>
                  <Image
                    style={{
                      width: 140,
                      height: 80,
                      resizeMode: "contain",
                      marginTop: 10,
                    }}
                    source={item.imageUrl}
                  />
                  <Text
                    style={{
                      fontSize: 20,
                      //   position: "absolute",
                      //   bottom: 60,
                      color: "red",
                      fontSize: 30,
                    }}
                  >
                    {item.title}
                  </Text>
                  <LinearGradient
                    colors={["#DE2E3B", "#E64440", "#EF5F46"]}
                    style={{ borderRadius: 30 }}
                  >
                    <TouchableOpacity
                      style={{
                        // backgroundColor: "red",
                        width: 130,
                        height: 40,
                        justifyContent: "center",
                        alignItems: "center",
                        borderRadius: 20,
                      }}
                    >
                      <Text style={{ color: "#fff", fontSize: 20 }}>
                        {" "}
                        {item.btnText}
                      </Text>
                    </TouchableOpacity>
                  </LinearGradient>
                </View>
              );
            }}
          />
        </View>
        <TouchableOpacity style={{ width: "50%", alignSelf: "center" }}>
          <LinearGradient
            style={{
              width: "100%",
              alignSelf: "center",
              paddingVertical: 10,
              borderRadius: 20,
              marginTop: 10,
              marginBottom: 10,
              justifyContent: "center",
              alignItems: "center",
            }}
            colors={["#DE2E3B", "#E64440", "#EF5F46"]}
          >
            <Text style={{ fontSize: 30, color: "#fff" }}>خیرات اجافیہ </Text>
          </LinearGradient>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  );
};

export default SeventhScreen;

const styles = StyleSheet.create({
  ButtonWrapper: {
    width: "100%",
    //height: 90,
    marginTop: 17,
    //paddingHorizontal: 10,
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "space-around",
  },
  Button: {
    width: "49%",
    height: 80,
    backgroundColor: "yellow",
    justifyContent: "space-around",
    alignItems: "center",
    flexDirection: "row",
    borderRadius: 40,
    paddingHorizontal: 20,
  },
  BtnText: {
    fontSize: 27,
    color: "#fff",
  },
  LargeBtnWrapper: {
    flex: 1,
    alignSelf: "center",
    width: "100%",

    marginTop: 17,
  },
  LargeBtn: {
    minHeight: 170,
    width: "40%",
    marginBottom: 20,
    paddingBottom: 10,
    backgroundColor: "#fff",
    borderRadius: 10,
    elevation: 12,
    shadowOffset: { width: 4, height: 5 },
    shadowOpacity: 0.3,
    shadowRadius: 5,
    alignItems: "center",
    justifyContent: "center",
  },
});
